import React, { Component } from 'react';

class RollDice extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sum: props.state
    };
  }

  rollTheDice = () => {
    const rollNum = Math.floor(Math.random() * 6) + 1;
    if (rollNum === 1) {
      this.setState({ sum: 'one' });
    }
    if (rollNum === 2) {
      this.setState({ sum: 'two' });
    }
    if (rollNum === 3) {
      this.setState({ sum: 'three' });
    }
    if (rollNum === 4) {
      this.setState({ sum: 'four' });
    }
    if (rollNum === 5) {
      this.setState({ sum: 'five' });
    }
    if (rollNum === 6) {
      this.setState({ sum: 'six' });
    }
    if (rollNum === 1) {
      this.setState({ sum: 'one' });
    }
    console.log(this.state.sum);
  };

  render() {
    return <button onClick={this.rollTheDice}>Roll Dice</button>;
  }
}

export default RollDice;
