import React, { Component } from 'react';
import Dice from './Dice';
import './RollDice.css';

class RollDice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      num: 'four',
      num2: 'two'
    };
    this.randNum = this.randNum.bind(this);
    this.newRandNum = this.newRandNum.bind(this);
  }

  randNum() {
    let rollNum = Math.floor(Math.random() * 6) + 1;
    // const rollNum2 = Math.floor(Math.random() * 6) + 1;
    if (rollNum === 1) {
      this.setState({ num: 'one' });
    }
    if (rollNum === 2) {
      this.setState({ num: 'two' });
    }
    if (rollNum === 3) {
      this.setState({ num: 'three' });
    }
    if (rollNum === 4) {
      this.setState({ num: 'four' });
    }
    if (rollNum === 5) {
      this.setState({ num: 'five' });
    }
    if (rollNum === 6) {
      this.setState({ num: 'six' });
    }
    let rollNum2 = Math.floor(Math.random() * 6) + 1;
    if (rollNum2 === 1) {
      this.setState({ num2: 'one' });
    }
    if (rollNum2 === 2) {
      this.setState({ num2: 'two' });
    }
    if (rollNum2 === 3) {
      this.setState({ num2: 'three' });
    }
    if (rollNum2 === 4) {
      this.setState({ num2: 'four' });
    }
    if (rollNum2 === 5) {
      this.setState({ num2: 'five' });
    }
    if (rollNum2 === 6) {
      this.setState({ num2: 'six' });
    }
    console.log(this.state.num);
    console.log(this.state.num2);
  }

  newRandNum() {
    let rollNumb = Math.floor(Math.random() * 6) + 1;
    let rollNumb2 = Math.floor(Math.random() * 6) + 1;

    const wordNumsArr = ['', 'one', 'two', 'three', 'four', 'five', 'six'];

    // const dieOne = wordNumsArr.filter((die, idx) => {
    //   if (rollNumb === idx) {
    //     return die;
    //   }
    // });

    wordNumsArr.forEach((die, idx) => {
      if (rollNumb === idx) {
        this.setState({ num: die });
      }
    });

    // console.log(dieOne);
  }

  render() {
    // console.log(this.state);

    return (
      <div className="container">
        <Dice info={this.state.num} />
        <Dice info={this.state.num2} />
        {/* <button >Roll Dice</button> */}
        <button onClick={this.randNum}>Roll Dice</button>
        <button onClick={this.newRandNum}>Roll Dice</button>
      </div>
    );
  }
}
export default RollDice;
