import React, { Component } from 'react';

class RollDice extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sum: props.state
    };
  }

  rollTheDice = () => {
    const rollNum = Math.floor(Math.random() * 6) + 1;
    if (rollNum === 1) {
      rollNum.slice('one');
    }
    console.log(rollNum);
  };

  render() {
    return <button onClick={this.rollTheDice}>Roll Dice</button>;
  }
}

export default RollDice;
