import React, { Component } from 'react';

class RollDice extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sum: props
    };
  }

  rollTheDice = () => {
    console.log(props.state);
  };

  render() {
    return <button onClick={this.rollTheDice}>Roll Dice</button>;
  }
}

export default RollDice;
