import React, { Component } from 'react';
import Dice from './Dice';
import './RollDice.css';

class RollDice extends Component {
  constructor(props) {
    super(props);
    this.state = {
      num: 'four',
      num2: 'two'
    };

    this.newRandNum = this.newRandNum.bind(this);
  }

  newRandNum() {
    let rollNumb = Math.floor(Math.random() * 6) + 2;
    let rollNumb2 = Math.floor(Math.random() * 6) + 2;

    const wordNumsArr = ['one', 'two', 'three', 'four', 'five', 'six'];

    wordNumsArr.forEach((die, idx) => {
      if (rollNumb === idx) {
        console.log(rollNumb);
        console.log(idx, die);
        // this.setState({ num: die });
      }
      if (rollNumb2 === idx) {
        // this.setState({ num2: die });
      }
    });
  }

  render() {
    return (
      <div className="container">
        <Dice info={this.state.num} />
        <Dice info={this.state.num2} />
        <button onClick={this.newRandNum}>Roll Dice</button>
      </div>
    );
  }
}
export default RollDice;
