import React, { Component } from 'react';

class Dice extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sum: props.state.num,
      sum2: props.state.num2
    };
  }

  render() {
    return (
      <div>
        <i className={`fas fa-dice-${this.state.sum}`}></i>
        <i className={`fas fa-dice-${this.state.sum2}`}></i>
        <button onClick={this.handleClick}>Roll Dice</button>
      </div>
    );
  }
}

export default Dice;
