import React, { Component } from 'react';
import './RollDice.css';

class RollDice extends Component {
  constructor(props) {
    super(props);

    this.state = {
      sum: props.state.num,
      sum2: props.state.num2
    };
  }

  handleClick = () => {
    const rollNum = Math.floor(Math.random() * 6) + 1;
    // const rollNum2 = Math.floor(Math.random() * 6) + 1;
    if (rollNum === 1) {
      this.setState({ sum: 'one' });
    }
    if (rollNum === 2) {
      this.setState({ sum: 'two' });
    }
    if (rollNum === 3) {
      this.setState({ sum: 'three' });
    }
    if (rollNum === 4) {
      this.setState({ sum: 'four' });
    }
    if (rollNum === 5) {
      this.setState({ sum: 'five' });
    }
    if (rollNum === 6) {
      this.setState({ sum: 'six' });
    }

    const rollNum2 = Math.floor(Math.random() * 6) + 1;
    if (rollNum2 === 1) {
      this.setState({ sum2: 'one' });
    }
    if (rollNum2 === 2) {
      this.setState({ sum2: 'two' });
    }
    if (rollNum2 === 3) {
      this.setState({ sum2: 'three' });
    }
    if (rollNum2 === 4) {
      this.setState({ sum2: 'four' });
    }
    if (rollNum2 === 5) {
      this.setState({ sum2: 'five' });
    }
    if (rollNum2 === 6) {
      this.setState({ sum2: 'six' });
    }
  };

  render() {
    return (
      <div>
        {/* <i className={`fas fa-dice-${this.state.sum}`}></i>
        <i className={`fas fa-dice-${this.state.sum2}`}></i> */}
        <button onClick={this.handleClick}>Roll Dice</button>
      </div>
    );
  }
}

export default RollDice;
